import React, { useState } from "react";
import { NavBar } from "./navbar";
import { Footer } from "./footer";
import './assets/styles/custompage.css'

export function CustomPage()
{
	const [methodChoice, setMethodChoice] = useState('')
	return (
		<>
			<NavBar />
			<main>
			<CustomPageForm />
			</main>
			<Footer />
		</>
	);
}

export function CustomPageForm()
{
	const [activeForm, setActiveForm] = useState(null);

	const handleRetour = () => {
		setActiveForm(null);
	};

	return (
		<>
			{!activeForm && (
			<>
				<h1 id='choiceTitle'>Choose a form</h1>
				<div id='choices'>
					<button className='choiceBtn' onClick={() => setActiveForm('getForm')}>Get Method Form</button>
					<button className='choiceBtn' onClick={() => setActiveForm('postForm')}>Post Method Form</button>
				</div>
			</>
			)}

			{activeForm && (
			<button id='returnBtn' onClick={handleRetour}> &#x2190; Retour </button>
			)}

			<CustomForm activeForm={activeForm}/>
		</>
	);
}

function CustomForm( {activeForm} )
{
	const methods = { 'getForm' : 'get', 'postForm' : 'post'};
	const method = methods[activeForm];
	return (
		<>
			{activeForm === 'getForm' && (
				<>
					<h2 className="formTitle">{method} Form</h2>
					<form className='customPageForms' action='/createcustom' method='get'>
						<label>
							Nom:
							<input type="text" name="nom" required/>
						</label>
						<label>
							Prenom:
							<input type="text" name="nom" required/>
						</label>
						<label>
							Date de naissance:
							<input type="date" name="nom" required/>
						</label>
						<button type="submit">Create custom page</button>
					</form>
				</>
			)}

			{activeForm === 'postForm' && (
				<>
					<form className='customPageForms' action='/createcustom' method='post'>
						<h2 className="formTitle">Post Form</h2>
						<label>
							Nom:
							<input type="text" name="nom" required/>
						</label>
						<button type="submit">Create custom page</button>
					</form>
				</>
			)}
		</>
	);
}
