import React, { useState } from "react";
import { NavBar } from "./navbar";
import { Footer } from "./footer";

export function CustomPage()
{
	const [methodChoice, setMethodChoice] = useState('')
	return (
		<>
			<NavBar />
			<main>
			<CustomPageForm />
			</main>
			<Footer />
		</>
	);
}

export function CustomPageForm()
{
	const [activeForm, setActiveForm] = useState(null);

	const handleRetour = () => {
		setActiveForm(null);
	};

	return (
		<div>

		  {!activeForm && (
			<>
			<h1>Choose a form</h1>
			<div>
			  <button onClick={() => setActiveForm('getForm')}>Get Method Form</button>
			  <button onClick={() => setActiveForm('postForm')}>Post Method Form</button>
			</div>
			</>
		  )}

		  {activeForm && (
			<button onClick={handleRetour}> Retour </button>
		  )}

		  <CustomForm activeForm={activeForm}/>
		</div>
	);
}

function CustomForm( {activeForm} )
{
	return (
		<>
			{activeForm === 'getForm' && (
				<form action='/createcustom' method='get'>
				<h2>Get Form</h2>
				<label>
					Nom:
					<input type="text" name="nom" />
				</label>
				<button type="submit">Soumettre Formulaire 1</button>
				</form>
			)}

			{activeForm === 'postForm' && (
			<form action='/createcustom' method=''>
				<h2>Post Form</h2>
				<label>
				Email:
				<input type="text" name="email" />
				</label>
				<button type="submit">Soumettre Formulaire 2</button>
			</form>
			)}
		</>
	);
}
