import React, { useState } from "react";
import { NavBar } from "./navbar";
import { Footer } from "./footer";

export function CustomPage()
{
	const [methodChoice, setMethodChoice] = useState('')
	return (
		<>
			<NavBar />
			<main>
			<CustomPageForm />
			</main>
			<Footer />
		</>
	);
}

export function CustomPageForm()
{
	const [activeForm, setActiveForm] = useState(null);

	const handleRetour = () => {
		setActiveForm(null);
	};

	return (
		<div>

		  {!activeForm && (
			<>
			<h1>Choose a form</h1>
			<div>
			  <button onClick={() => setActiveForm('getForm')}>Post Method Form</button>
			  <button onClick={() => setActiveForm('postForm')}>Get Method Form</button>	
			</div>
			</>
		  )}

		  {activeForm && (
			<button onClick={handleRetour}> Retour </button>
		  )}

		  {activeForm === 'getForm' && (
			<form>
			  <h2>Get Form</h2>
			  <label>
				Nom:
				<input type="text" name="nom" />
			  </label>
			  <button type="submit">Soumettre Formulaire 1</button>
			</form>
		  )}

		  {activeForm === 'postForm' && (
			<form>
			  <h2>Post Form</h2>
			  <label>
				Email:
				<input type="text" name="email" />
			  </label>
			  <button type="submit">Soumettre Formulaire 2</button>
			</form>
		  )}
		</div>
	);
}
