import React, { useState } from "react";
import { NavBar } from "./navbar";
import { Footer } from "./footer";
import './assets/styles/custompage.css'

export function CustomPage()
{
	const [methodChoice, setMethodChoice] = useState('')
	return (
		<>
			<NavBar />
			<main>
			<CustomPageForm />
			</main>
			<Footer />
		</>
	);
}

export function CustomPageForm()
{
	const [activeForm, setActiveForm] = useState(null);

	const handleRetour = () => {
		setActiveForm(null);
	};

	return (
		<div>

		  {!activeForm && (
			<>
			<h1 id='choiceTitle'> Choose a form </h1>
			<div id='choices'>
			  <button className='choiceBtn' onClick={() => setActiveForm('getForm')}>Get Method Form</button>
			  <button className='choiceBtn' onClick={() => setActiveForm('postForm')}>Post Method Form</button>
			</div>
			</>
		  )}

		  {activeForm && (
			<button id='returnBtn' onClick={handleRetour}> Retour </button>
		  )}

		  <CustomForm activeForm={activeForm}/>
		</div>
	);
}

function CustomForm( {activeForm} )
{
	return (
		<>
			{activeForm === 'getForm' && (
				<form classaction='/createcustom' method='get'>
					<h2>Get Form</h2>
					<label>
						Nom:
						<input type="text" name="nom" required/>
					</label>
					<button type="submit">Create custom page</button>
				</form>
			)}

			{activeForm === 'postForm' && (
				<form action='/createcustom' method='post'>
					<h2>Post Form</h2>
					<label>
					Email:
					<input type="text" name="nom" required/>
					</label>
					<button type="submit">Create custom page</button>
				</form>
			)}
		</>
	);
}
