import React, { useState } from "react";
import { NavBar } from "./navbar";
import { Footer } from "./footer";

export function CustomPage()
{
	const [methodChoice, setMethodChoice] = useState('')
	return (
		<>
			<NavBar />
			<main>
			<CustomPageForm />
			</main>
			<Footer />
		</>
	);
}

export function CustomPageForm()
{
	const [activeForm, setActiveForm] = useState(null);

	const handleRetour = () => {
		setActiveForm(null);
	};

	return (
		<div>
		  <h1>Choisissez un formulaire</h1>

		  {!activeForm && (
			<div>
			  <button onClick={() => setActiveForm('form1')}>Afficher Formulaire 1</button>
			  <button onClick={() => setActiveForm('form2')}>Afficher Formulaire 2</button>
			</div>
		  )}

		  {activeForm && (
			<button onClick={handleRetour}> Retour </button>
		  )};

		  {activeForm === 'form1' && (
			<form>
			  <h2>Formulaire 1</h2>
			  <label>
				Nom:
				<input type="text" name="nom" />
			  </label>
			  <button type="submit">Soumettre Formulaire 1</button>
			</form>
		  )}

		  {activeForm === 'form2' && (
			<form>
			  <h2>Formulaire 2</h2>
			  <label>
				Email:
				<input type="text" name="email" />
			  </label>
			  <button type="submit">Soumettre Formulaire 2</button>
			</form>
		  )}
		</div>
	);
}
