import React from 'react';
import { NavBar } from './navbar';
import { Footer } from './footer';
import ReactDOM from 'react-dom/client';


const isThemeSet = () =>
{
	console.log("JE PASSE ICI");
	const cookies = document.cookie;
	return cookies.split(';').some(el => el.trim() === "theme=light");
}

export function ChooseTheme()
{
	return (
		<>
		<NavBar />
		<main>
			{isThemeSet() ? <DarkTheme /> : <LightTheme /> }
		</main>
		<Footer />
		</>
	)
}

function LightTheme()
{
	return (
		<>
		<form action='/themelight' method="post">
			<button type="submit">Switch to Light theme</button>
			<input type="hidden" name="theme" value="light" />
		</form>
		</>
	)
}

function DarkTheme()
{
	return (
		<>
		<form action='/themedark' method="post">
			<button type="submit">Switch to Dark theme</button>
			<input type="hidden" name="theme" value="dark" />
		</form>
		</>
	)
}
